package org.test1;

public class encapsulate {
	
	private String name;
	private int id;
	private long phno;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

	}
//setter
	public void setName(String name) {
		this.name = name;
	}

	public void setId(int id) {
		this.id = id;
	}

	public void setPhno(long phno) {
		this.phno = phno;
	}
	//getter
	public String getName() {
		return name;
	}
	public int getId() {
		return id;
	}
	public long getPhno() {
		return phno;
	}
	

	
}
